Ext.define('cerodatax.view.escritorio.FormAutenticacionViewController1', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.escritorioformchangepassword',

    onWindowsChangePasswordAfterRender: function(component, eOpts) {
        component.keyboardMapping = new Ext.util.KeyMap({
            target: component.el,
            key: Ext.event.Event.ENTER,
            fn: function (keyCode, event) {
                var defaultButton =  Ext.ComponentQuery.query('#buttonChangePassword')[0];
                if (!defaultButton.isDisabled())
                defaultButton.fireEvent('click', defaultButton);
            },
            scope: component
        });
    }

});
